document.getElementById('addInstituteButton').addEventListener('click', function() {
    // Get the institute fields container
    const instituteFields = document.getElementById('instituteFields');

    // Create a new set of fields
    const newInstituteGroup = document.createElement('div');
    newInstituteGroup.classList.add('row', 'institute-group');

    newInstituteGroup.innerHTML = `


            From *






            To *





            University / College *





            Award / Course Title *





            Academic Attachments *


    `;

    // Append the new set of fields to the form
    instituteFields.appendChild(newInstituteGroup);
});

function hideOptionsForProposer(proposer_selection) {
    if (proposer_selection !== '') {
        var selectElements = ['seconder1_selection'];
        selectElements.forEach(function(elementId) {
            $('#' + elementId + ' option[value="' + proposer_selection + '"]').hide();
        });
    }
}

function hideOptionsForSeconder1(seconder1_selection) {
    if (seconder1_selection !== '') {
        var selectElements = ['proposer_selection'];
        selectElements.forEach(function(elementId) {
            $('#' + elementId + ' option[value="' + seconder1_selection + '"]').hide();
        });
    }
}

