document.getElementById('passportPhotoInput').addEventListener('change', function() {
    const passportPhotoInput = this;
    if (passportPhotoInput.files.length > 0) {
        const fileSize = passportPhotoInput.files[0].size; // in bytes
        const maxSize = 1000000; // 1MB
        if (fileSize > maxSize) {
            alert('File size exceeds the limit of 1MB. Please choose a smaller file.');
            passportPhotoInput.value = ''; // Clear the file input
        }
    }
});

document.getElementById("company_section").style.display = "none";
document.getElementById("individual_section").style.display = "none";
document.getElementById("other_select_option").style.display = "none";
document.getElementById("sponsorship_section").style.display = "none";

let individual_fields = ['member_name', 'date_of_birth', 'gender_class', 'mobile_individual', 'member_email', 'country_selection', 'passportPhotoInput', 'current_employer', 'current_position', 'postal_address', 'individual_selection', 'sponsorship_type']

document.getElementById("member_type_selection").addEventListener("change", function(){
    if(this.value==="company"){
        document.getElementById("company_section").style.display = "block";
        document.getElementById("individual_section").style.display = "none";

        $.each(company_fields, function (i, comp) {
            try {document.getElementById(comp).setAttribute('required', '1');} catch (e) {}
        });

        $.each(individual_fields, function (i, ind) {
            try {document.getElementById(ind).removeAttribute('required');} catch (e) {}
        });

    }else if(this.value=="person"){
        document.getElementById("company_section").style.display = "none";
        document.getElementById("individual_section").style.display = "block";

        $.each(individual_fields, function (i, ind) {
            try {document.getElementById(ind).setAttribute('required', '1');} catch (e) {}
        });

        $.each(company_fields, function (i, comp) {
            try {document.getElementById(comp).removeAttribute('required');} catch (e) {}
        });

    }
});



document.getElementById("sponsorship_type").addEventListener("change", function(){
    if(this.value=="company"){
    document.getElementById("sponsorship_section").style.display = "block";
    try {document.getElementById("sponsorship_section").setAttribute('required', '1');} catch (e) {}
}else{
    document.getElementById("sponsorship_section").style.display = "none";
    try {document.getElementById("sponsorship_section").removeAttribute('required');} catch (e) {}
}
});
